import React, { useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import EditPage from "./components/EditPage";
import SearchPage from "./components/SearchPage";
import ReadPage from "./components/ReadPage";
import Sidebar from "./components/Sidebar";
import "./App.css";

function App() {
  const [playlist, setPlaylist] = useState([]);

  const updateSidebar = (updatedPlaylist) => {
    setPlaylist(updatedPlaylist);
  };

  return (
    <Router>
      <div className="app">
        <Sidebar playlist={playlist} />
        <Routes>
          <Route
            path="/edit"
            element={<EditPage updateSidebar={updateSidebar} />}
          />
          <Route path="/search" element={<SearchPage updateSidebar={updateSidebar} />} />
          <Route path="/read" element={<ReadPage playlist={playlist} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
