import React, { useEffect, useState } from "react";
import { getPlaylist, deleteTrackFromPlaylist } from "../services/PlaylistService";
import "./EditPage.css";

const EditPage = () => {
  const [playlist, setPlaylist] = useState([]);

  useEffect(() => {
    const fetchPlaylist = async () => {
      const data = await getPlaylist();
      setPlaylist(data);
    };
    fetchPlaylist();
  }, []);

  const handleDelete = async (id) => {
    try {
      await deleteTrackFromPlaylist(id);
      const updatedPlaylist = playlist.filter((track) => track.id !== id);
      setPlaylist(updatedPlaylist);
    } catch (error) {
      console.error("Error deleting track:", error);
      alert("Failed to delete track.");
    }
  };

  return (
    <div className="edit-page">
      <h1>Edit Playlist</h1>
      <div className="playlist">
        {playlist.map((track) => (
          <div key={track.id} className="track-card">
            <img src={track.image} alt={track.name} className="track-image" />
            <div className="track-info">
              <h4>{track.name}</h4>
              <p>{track.artist}</p>
            </div>
            <button onClick={() => handleDelete(track.id)} className="delete-btn">
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EditPage;
