import React, { useEffect, useState } from "react";
import { getPlaylist, deleteTrackFromPlaylist } from "../services/PlaylistService";
import { useNavigate } from "react-router-dom"; // React Router 사용
import "./EditPage.css"; // 스타일 연결

const EditPage = ({ updateSidebar }) => {
  const [playlist, setPlaylist] = useState([]);
  const navigate = useNavigate(); // 페이지 전환을 위한 네비게이션 훅

  // 초기 플레이리스트 가져오기
  useEffect(() => {
    const fetchPlaylist = async () => {
      try {
        const data = await getPlaylist();
        setPlaylist(data);
      } catch (error) {
        console.error("Error fetching playlist:", error);
        alert("Failed to load playlist.");
      }
    };
    fetchPlaylist();
  }, []);

  // 삭제 버튼 핸들러
  const handleDelete = async (trackId) => {
    try {
      await deleteTrackFromPlaylist(trackId); // API 호출로 삭제
      const updatedPlaylist = playlist.filter((track) => track.id !== trackId); // 상태에서 삭제
      setPlaylist(updatedPlaylist); // 로컬 상태 업데이트
      updateSidebar(updatedPlaylist); // 사이드바 상태 동기화
      alert("Track deleted successfully!");
    } catch (error) {
      console.error("Error deleting track:", error);
      alert("Failed to delete track.");
    }
  };

  return (
    <div className="edit-page">
      <h1>Edit Playlist</h1>
      <p>Edit your favorite tracks</p>
      <div className="playlist-container">
        {playlist.map((track) => (
          <div key={track.id} className="playlist-card">
            <div className="track-info">
              <img src={track.image} alt={track.name} className="album-cover" />
              <div>
                <h3>{track.name}</h3>
                <p>{track.artist}</p>
              </div>
            </div>
            <button
              className="delete-btn"
              onClick={() => handleDelete(track.id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
      <div className="page-buttons">
        <button onClick={() => navigate("/search")}>
          Go to Search Page
        </button>
        <button onClick={() => navigate("/read")}>
          Go to Read Page
        </button>
      </div>
    </div>
  );
};

export default EditPage;
