import React, { useEffect, useState } from "react";
import { getPlaylist, deleteTrack } from "../services/PlaylistService";
import "./EditPage.css";

const EditPage = ({ updateSidebar }) => {
  const [playlist, setPlaylist] = useState([]);

  useEffect(() => {
    const fetchPlaylist = async () => {
      const data = await getPlaylist();
      setPlaylist(data);
      updateSidebar(data); // 초기 사이드바 업데이트
    };
    fetchPlaylist();
  }, [updateSidebar]);

  const handleDelete = async (id) => {
    try {
      await deleteTrack(id); // 트랙 삭제 API 호출
      alert("Track deleted successfully!");

      // 삭제 후 업데이트된 플레이리스트 가져오기
      const updatedPlaylist = await getPlaylist();
      setPlaylist(updatedPlaylist); // 상태 업데이트
      updateSidebar(updatedPlaylist); // 사이드바 업데이트
    } catch (error) {
      console.error("Error deleting track:", error);
      alert("Failed to delete track.");
    }
  };

  return (
    <div className="edit-page">
      <header className="edit-header">
        <h1>Edit Playlist</h1>
        <h2>Edit your favorite tracks</h2>
      </header>
      <div className="playlist-container">
        {playlist.map((track, index) => (
          <div key={index} className="playlist-card">
            <div className="track-info">
              <img className="album-image" src={track.image} alt={track.name} />
              <div>
                <h4>{track.name}</h4>
                <p>{track.artist}</p>
              </div>
            </div>
            <button
              className="delete-btn"
              onClick={() => handleDelete(track.id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
      <div className="edit-buttons">
        <button className="nav-btn" onClick={() => (window.location.href = "/")}>
          Go to Search Page
        </button>
        <button
          className="nav-btn"
          onClick={() => (window.location.href = "/read")}
        >
          Go to Read Page
        </button>
      </div>
    </div>
  );
};

export default EditPage;
