import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import SearchPage from "./components/SearchPage";
import ReadPage from "./components/ReadPage";
import EditPage from "./components/EditPage";
import Sidebar from "./components/SideBar";
import { getPlaylist } from "./services/PlaylistService";
import "./App.css";

const App = () => {
  const [playlist, setPlaylist] = useState([]); // 전역 플레이리스트 상태

  // 초기 플레이리스트 로드
  useEffect(() => {
    const fetchPlaylist = async () => {
      try {
        const data = await getPlaylist();
        setPlaylist(data); // 초기 데이터 로드
      } catch (error) {
        console.error("Error loading playlist:", error);
      }
    };
    fetchPlaylist();
  }, []);

  // 사이드바를 업데이트하는 함수
  const updateSidebar = async () => {
    try {
      const updatedPlaylist = await getPlaylist(); // 최신 데이터 가져오기
      setPlaylist(updatedPlaylist); // 상태 업데이트
    } catch (error) {
      console.error("Error updating sidebar:", error);
    }
  };

  return (
    <Router>
      <div className="app">
        {/* 메인 콘텐츠와 사이드바 */}
        <div className="main-content">
          <Routes>
            <Route
              path="/"
              element={<SearchPage updateSidebar={updateSidebar} />} // updateSidebar 전달
            />
            <Route
              path="/read"
              element={<ReadPage updateSidebar={updateSidebar} />} // updateSidebar 전달
            />
            <Route
              path="/edit"
              element={<EditPage updateSidebar={updateSidebar} />} // updateSidebar 전달
            />
            <Route
              path="/search&add"
              element={<SearchPage updateSidebar={updateSidebar} />} // updateSidebar 전달
            />
          </Routes>
        </div>
        {/* 사이드바 */}
        <Sidebar playlist={playlist} />
      </div>
    </Router>
  );
};

export default App;
