import React from "react";
import { useNavigate } from "react-router-dom";
import { deleteTrackFromPlaylist } from "../services/PlaylistService";
import "./SideBar.css";

const Sidebar = ({ playlist, updatePlaylist }) => {
  const navigate = useNavigate(); // ÌéòÏù¥ÏßÄ Ïù¥ÎèôÏùÑ ÏúÑÌïú React Router ÌõÖ

  const handleDelete = async (id) => {
    try {
      await deleteTrackFromPlaylist(id);
      // ÏÇ≠Ï†ú ÌõÑ UI ÏóÖÎç∞Ïù¥Ìä∏
      const updatedPlaylist = playlist.filter((track) => track.id !== id);
      updatePlaylist(updatedPlaylist);
      alert("Track deleted successfully!");
    } catch (error) {
      console.error("Error deleting track:", error);
      alert("Failed to delete track.");
    }
  };

  return (
    <div className="rightside-menu">
      <div className="avatar">
        <div className="icon">üë©üèª‚Äçü¶∞</div>
        <p>Leeyunji</p>
        <button className="edit-btn">‚úèÔ∏è</button>
      </div>

      <nav className="menu">
        <ul>
          <li className="menu-item active">üè† Home</li>
          <li className="menu-item">üéµ My Playlist</li>
          <ul>
            {playlist.map((track) => (
              <li key={track.id} className="playlist-item">
                {track.name} - {track.artist}
                <button onClick={() => handleDelete(track.id)}>Delete</button>
              </li>
            ))}
          </ul>
        </ul>
      </nav>

      <div className="sidebar-buttons">
        {/* Search & Add Î≤ÑÌäº */}
        <button
          className="sidebar-btn"
          onClick={() => navigate("/search")} // Í≤ÄÏÉâÍ≥º Ï∂îÍ∞Ä ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        >
          Search & Add
        </button>
      </div>
    </div>
  );
};

export default Sidebar;
