import React from "react";
import { useNavigate } from "react-router-dom";
import { deleteTrackFromPlaylist } from "../services/PlaylistService";
import "./SideBar.css";

const Sidebar = ({ playlist, updatePlaylist }) => {
  const navigate = useNavigate(); // 페이지 이동을 위한 React Router 훅

  const handleDelete = async (id) => {
    try {
      await deleteTrackFromPlaylist(id);
      // 삭제 후 UI 업데이트
      const updatedPlaylist = playlist.filter((track) => track.id !== id);
      updatePlaylist(updatedPlaylist);
      alert("Track deleted successfully!");
    } catch (error) {
      console.error("Error deleting track:", error);
      alert("Failed to delete track.");
    }
  };

  return (
    <div className="sidebar">
      <h2>My Info</h2>
      <ul>
        {playlist.map((track) => (
          <li key={track.id}>
            <img src={track.image} alt={track.name} width="50" />
            <div>
              <h4>{track.name}</h4>
              <p>{track.artist}</p>
              <button onClick={() => handleDelete(track.id)}>Delete</button>
            </div>
          </li>
        ))}
      </ul>
      <div className="sidebar-buttons">
        <button onClick={() => navigate("/")}>Search & Add</button>
        <button onClick={() => navigate("/read")}>Read Playlist</button>
        <button onClick={() => navigate("/edit")}>Edit Playlist</button>
      </div>
    </div>
  );
};

export default Sidebar;
