import React, { useEffect, useState } from "react";
import { getPlaylist, deleteTrackFromPlaylist } from "../services/PlaylistService";
import "./EditPage.css";

const EditPage = ({ updateSidebar }) => {
  const [playlist, setPlaylist] = useState([]);
  const [error, setError] = useState(false); // 에러 상태 관리 추가

  // 초기 플레이리스트 로드
  useEffect(() => {
    const fetchPlaylist = async () => {
      try {
        const data = await getPlaylist();
        setPlaylist(data); // 로컬 상태 업데이트
        updateSidebar(data); // 사이드바와 동기화
      } catch (error) {
        console.error("Error fetching playlist:", error);
        setError(true); // 에러 상태 업데이트
      }
    };
    fetchPlaylist();
  }, [updateSidebar]);

  const handleDelete = async (trackId) => {
    try {
      // API를 통해 서버에서 곡 삭제
      const response = await deleteTrackFromPlaylist(trackId);

      if (response) {
        // 로컬 상태에서 곡 제거
        const updatedPlaylist = playlist.filter((track) => track.id !== trackId);
        setPlaylist(updatedPlaylist);

        // 사이드바 상태 동기화
        if (typeof updateSidebar === "function") {
          updateSidebar(updatedPlaylist);
        }

        // 성공 메시지
        alert("Track deleted successfully!");
      } else {
        // 서버 응답이 없을 경우 에러 처리
        throw new Error("No response from the server.");
      }
    } catch (error) {
      console.error("Error deleting track:", error);
      alert("Failed to delete track. Please try again.");
    }
  };


  return (
    <div className="edit-page">
      <h1>Edit Playlist</h1>
      <p>Edit your favorite tracks</p>
      {error && <p className="error-message">Failed to load playlist. Please try again later.</p>} {/* 에러 메시지 */}
      <div className="playlist-container">
        {playlist.map((track) => (
          <div key={track.id} className="playlist-card">
            <div className="track-info">
              <img src={track.image} alt={track.name} className="album-cover" />
              <div>
                <h3>{track.name}</h3>
                <p>{track.artist}</p>
              </div>
            </div>
            <button
              className="delete-btn"
              onClick={() => handleDelete(track.id)}
            >
              Delete
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EditPage;
