import React, { useState } from "react";
import { searchTracks } from "../services/SpotifyService";
import SearchResults from "./SearchResults"; // 검색 결과 컴포넌트
import "./SearchPage.css"; // 스타일 추가

const SearchPage = () => {
  const [searchTerm, setSearchTerm] = useState(""); // 검색어 상태
  const [tracks, setTracks] = useState([]); // 검색 결과 상태

  const handleSearch = async () => {
    if (!searchTerm.trim()) {
      alert("Please enter a search term.");
      return;
    }

    try {
      const results = await searchTracks(searchTerm);
      setTracks(results);
    } catch (error) {
      console.error("Error fetching search results:", error);
      alert("Failed to fetch search results. Please try again.");
    }
  };

  const handleAddTrack = (track) => {
    alert(`Track "${track.name}" by ${track.artists[0]?.name} added to the playlist!`);
    // 여기에서 추가 작업을 처리할 수 있습니다.
  };

  return (
    <div className="search-page">
      <h1>Search Tracks</h1>
      <div className="search-bar">
        <input
          type="text"
          placeholder="Enter a track or artist name..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      <SearchResults tracks={tracks} onAdd={handleAddTrack} />
    </div>
  );
};

export default SearchPage;
