import React, { useEffect, useState } from "react";
import { getPlaylist, deleteTrackFromPlaylist } from "../services/PlaylistService";
import "./EditPage.css"; // 스타일 연결

const EditPage = ({ updateSidebar }) => {
  const [playlist, setPlaylist] = useState([]);

  useEffect(() => {
    const fetchPlaylist = async () => {
      const data = await getPlaylist();
      setPlaylist(data);
    };
    fetchPlaylist();
  }, []);

  const handleDelete = async (trackId) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this track?");
    if (confirmDelete) {
      try {
        await deleteTrackFromPlaylist(trackId); // 삭제 API 호출
        const updatedPlaylist = playlist.filter((track) => track.id !== trackId);
        setPlaylist(updatedPlaylist);
        updateSidebar(updatedPlaylist); // 사이드바 업데이트
        alert("Track deleted successfully!");
      } catch (error) {
        console.error("Error deleting track:", error);
        alert("Failed to delete the track.");
      }
    }
  };

  return (
    <div className="edit-page">
      <header className="edit-header">
        <img src="/img/edit_logo.png" alt="Edit Playlist" className="edit-logo" />
        <h1>EDIT PAGE</h1>
        <h2>Edit Playlist</h2>
      </header>
      <div className="playlist-container">
        {playlist.map((track) => (
          <div key={track.id} className="playlist-card">
            <div className="track-info">
              <img className="album-image" src={track.image} alt={track.name} />
              <div>
                <h4>{track.name}</h4>
                <p>{track.artist}</p>
              </div>
            </div>
            <button className="delete-btn" onClick={() => handleDelete(track.id)}>
              Delete
            </button>
          </div>
        ))}
      </div>
      <div className="edit-buttons">
        <button className="nav-btn" onClick={() => (window.location.href = "/")}>
          Go to Search Page
        </button>
        <button className="nav-btn" onClick={() => (window.location.href = "/read")}>
          Go to Read Page
        </button>
      </div>
    </div>
  );
};

export default EditPage;
