import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SideBar.css"; // Ïä§ÌÉÄÏùº Ïó∞Í≤∞

const Sidebar = ({ playlist }) => {
  const navigate = useNavigate();
  const [isPlaylistOpen, setIsPlaylistOpen] = useState(false);

  // ÌîåÎ†àÏù¥Î¶¨Ïä§Ìä∏ Ïó¥Î¶º/Îã´Ìûò Ìï∏Îì§Îü¨
  const handleMyPlaylistClick = () => {
    setIsPlaylistOpen((prevState) => !prevState);
    navigate("/read");
  };

  // ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [isEditing, setIsEditing] = useState(false); // ÌîÑÎ°úÌïÑ ÏàòÏ†ï Ï∞Ω ÌëúÏãú Ïó¨Î∂Ä
  const [profileName, setProfileName] = useState("Leeyunji"); // ÌîÑÎ°úÌïÑ Ïù¥Î¶Ñ
  const [profileEmoji, setProfileEmoji] = useState("üë©üèª‚Äçü¶∞"); // ÌîÑÎ°úÌïÑ Ïù¥Î™®ÏßÄ

  // ÌîÑÎ°úÌïÑ Ï†ÄÏû• Ìï∏Îì§Îü¨
  const handleSaveProfile = () => {
    const nameInput = document.getElementById("profile-name-input").value.trim();
    const emojiSelect = document.getElementById("profile-emoji-select").value;

    if (nameInput) {
      setProfileName(nameInput);
    }
    setProfileEmoji(emojiSelect);

    // ÏàòÏ†ï Ï∞Ω Îã´Í∏∞
    setIsEditing(false);
  };

  return (
    <div className="rightside-menu">
      {/* ÏïÑÎ∞îÌÉÄ ÏòÅÏó≠ */}
      <div className="avatar">
        <div className="icon">
          <p id="profile-emoji">{profileEmoji}</p>
        </div>
        <p id="profile-name">{profileName}</p>
        <button
          id="edit-profile-btn"
          className="edit-btn"
          onClick={() => setIsEditing((prev) => !prev)}
        >
          ‚úèÔ∏è
        </button>
        {isEditing && (
          <div id="profile-edit-container" className="profile-edit-container">
            <label htmlFor="profile-emoji-select">Choose Emoji:</label>
            <select
              id="profile-emoji-select"
              className="scrollable-emoji"
              defaultValue={profileEmoji}
            >
              <optgroup label="Female">
                <option value="üë©üèª‚Äçü¶∞">üë©üèª‚Äçü¶∞</option>
                <option value="üë©üèª">üë©üèª</option>
                <option value="üë©üèª‚Äçü¶±">üë©üèª‚Äçü¶±</option>
                <option value="üë©üèª‚Äçü¶≥">üë©üèª‚Äçü¶≥</option>
                <option value="üë©üèª‚Äçü¶≤">üë©üèª‚Äçü¶≤</option>
              </optgroup>
              <optgroup label="Male">
                <option value="üßëüèª">üßëüèª</option>
                <option value="üë±üèª">üë±üèª</option>
                <option value="üßîüèª">üßîüèª</option>
                <option value="üßëüèª‚Äçü¶±">üßëüèª‚Äçü¶±</option>
                <option value="üë®üèª‚Äçü¶∞">üë®üèª‚Äçü¶∞</option>
                <option value="üë®üèª‚Äçü¶±">üë®üèª‚Äçü¶±</option>
                <option value="üë®üèª‚Äçü¶≥">üë®üèª‚Äçü¶≥</option>
                <option value="üë®üèª‚Äçü¶≤">üë®üèª‚Äçü¶≤</option>
              </optgroup>
              <optgroup label="Jobs">
                <option value="üßë‚Äçüç≥">üßë‚Äçüç≥</option>
                <option value="üë®‚Äçüé®">üë®‚Äçüé®</option>
                <option value="üë©‚Äçüé®">üë©‚Äçüé®</option>
                <option value="üßë‚Äçüî¨">üßë‚Äçüî¨</option>
                <option value="üëÆüèª‚Äç‚ôÄÔ∏è">üëÆüèª‚Äç‚ôÄÔ∏è</option>
                <option value="üë©üèª‚Äç‚öïÔ∏è">üë©üèª‚Äç‚öïÔ∏è</option>
                <option value="üë©üèª‚Äçüíª">üë©üèª‚Äçüíª</option>
              </optgroup>
            </select>
            <label htmlFor="profile-name-input">Enter Name:</label>
            <input
              id="profile-name-input"
              type="text"
              placeholder="Enter new name"
              defaultValue={profileName}
            />
            <button
              id="save-profile-btn"
              className="save-btn"
              onClick={handleSaveProfile}
            >
              Save
            </button>
          </div>
        )}
      </div>

      {/* Î©îÎâ¥ ÏòÅÏó≠ */}
      <nav className="menu">
        <ul>
          {/* Ìôà Î©îÎâ¥ */}
          <li className="menu-item active" onClick={() => navigate("/")}>
            üè† Home
          </li>
          {/* ÎßàÏù¥ ÌîåÎ†àÏù¥Î¶¨Ïä§Ìä∏ */}
        <li className="menu-item" onClick={handleMyPlaylistClick}>
          üéµ My Playlist
          <ul id="playlist-folder" className={`playlist-folder ${isPlaylistOpen ? "open" : ""}`}>
            {playlist.map((track) => (
              <li key={track.id} className="playlist-item">
                {track.name} - {track.artist}
              </li>
            ))}
          </ul>
        </li>

          {/* Í≤ÄÏÉâ Î∞è Ï∂îÍ∞Ä */}
          <li
            className="menu-item"
            onClick={() => navigate("/edit")}
          >
            ‚úèÔ∏è Edit
          </li>
          {/* Search & Add */}
          <li
            className="menu-item"
            onClick={() => navigate("/Search&add")} // Search & Add ÌÅ¥Î¶≠ Ïãú /search&add Í≤ΩÎ°úÎ°ú Ïù¥Îèô
          >
            ‚ûï Search & Add
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Sidebar;
