import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import SearchPage from "./components/SearchPage";
import ReadPage from "./components/ReadPage";
import EditPage from "./components/EditPage";
import Sidebar from "./components/SideBar";
import { getPlaylist } from "./services/PlaylistService";
import "./App.css";

const App = () => {
  const [playlist, setPlaylist] = useState([]);

  // 초기 플레이리스트 데이터 로드
  useEffect(() => {
    const fetchPlaylist = async () => {
      try {
        const data = await getPlaylist();
        setPlaylist(data); // 상태에 로드한 데이터를 저장
      } catch (error) {
        console.error("Error loading playlist:", error);
        alert("Failed to load playlist. Please refresh the page.");
      }
    };
    fetchPlaylist();
  }, []);

  return (
    <Router>
      <div className="app">
        {/* 사이드바 */}
        <Sidebar playlist={playlist} updatePlaylist={setPlaylist} />
        {/* 메인 컨텐츠 */}
        <div className="main-content">
          <Routes>
            <Route
              path="/"
              element={<SearchPage updatePlaylist={setPlaylist} />}
            />
            <Route
              path="/read"
              element={<ReadPage updatePlaylist={setPlaylist} />}
            />
            <Route
              path="/edit"
              element={<EditPage updatePlaylist={setPlaylist} />}
            />
            <Route
              path="/search&add"
              element={<SearchPage updatePlaylist={setPlaylist} />}
            />
          </Routes>
        </div>
      </div>
    </Router>
  );
};

export default App;
