import React, { useState } from "react";

const SearchResults = ({ tracks, onAdd }) => {
  const [currentAudio, setCurrentAudio] = useState(null); // 현재 재생 중인 오디오 상태

  const handlePlayPreview = (url) => {
    if (!url) {
      alert("Preview not available.");
      return;
    }

    // 기존 재생 중인 오디오 중지
    if (currentAudio) {
      currentAudio.pause();
      setCurrentAudio(null);
    }

    // 새로운 오디오 재생
    const audio = new Audio(url);
    audio.play();
    setCurrentAudio(audio);
  };

  const handleStopPreview = () => {
    if (currentAudio) {
      currentAudio.pause();
      setCurrentAudio(null);
    }
  };

  return (
    <div className="search-results">
      {tracks && tracks.length > 0 ? (
        tracks.map((track) => (
          <div key={track.id} className="track-card">
            <img
              src={track.album.images[0]?.url}
              alt={track.name}
              style={{ width: "100px", height: "100px" }}
            />
            <h4>{track.name}</h4>
            <p>{track.artists[0]?.name}</p>
            <button onClick={() => onAdd(track)}>Add</button>
            <button onClick={() => handlePlayPreview(track.preview_url)}>
              Play
            </button>
            <button onClick={handleStopPreview}>Stop</button>
          </div>
        ))
      ) : (
        <p>No tracks found. Please search for a song.</p>
      )}
    </div>
  );
};

export default SearchResults;
