import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import SearchPage from "./components/SearchPage";
import ReadPage from "./components/ReadPage";
import EditPage from "./components/EditPage";
import Sidebar from "./components/SideBar";
import Layout from "./components/Layout"; // Layout 컴포넌트 추가
import { getPlaylist } from "./services/PlaylistService";
import "./App.css";

const App = () => {
  const [playlist, setPlaylist] = useState([]);

  useEffect(() => {
    const fetchPlaylist = async () => {
      try {
        const data = await getPlaylist();
        setPlaylist(data);
      } catch (error) {
        console.error("Error loading playlist:", error);
      }
    };
    fetchPlaylist();
  }, []);

  const updatePlaylist = (newPlaylist) => {
    setPlaylist(newPlaylist);
  };

  return (
    <Router>
      <div className="app">
        <div className="main-content">
          <Routes>
            <Route
              path="/"
              element={
                <Layout>
                  <SearchPage updatePlaylist={updatePlaylist} />
                </Layout>
              }
            />
            <Route
              path="/read"
              element={
                <Layout>
                  <ReadPage updatePlaylist={updatePlaylist} />
                </Layout>
              }
            />
            <Route
              path="/edit"
              element={
                <Layout>
                  <EditPage updateSidebar={updatePlaylist} />
                </Layout>
              }
            />
            <Route
              path="/search&add"
              element={
                <Layout>
                  <SearchPage updatePlaylist={updatePlaylist} />
                </Layout>
              }
            />
          </Routes>
        </div>
        <Sidebar playlist={playlist} />
      </div>
    </Router>
  );
};

export default App;
